shader.vertex:
attribute vec3 Position;
attribute vec4 Color;
attribute vec2 TexCoord;
attribute vec4 RenderData;
attribute float Scale;

attribute vec3 TintColor;
attribute float Intensity;

uniform mat4 Transform;

varying vec4 Color0;
varying vec2 TexCoord0;
varying vec4 RenderDataOut;
varying float ScaleOut;

varying vec3 TintColorOut;
varying float IntensityOut;

void main(void) {
    Color0 = Color;
    TexCoord0 = TexCoord;
    RenderDataOut = RenderData;
    ScaleOut = Scale;
    TintColorOut = TintColor;
    IntensityOut = Intensity;
    gl_Position = Transform * vec4(Position.xyz, 1.0);
}

shader.fragment:
varying lowp vec4 Color0;
varying mediump vec2 TexCoord0;
varying lowp vec4 RenderDataOut;
varying lowp float ScaleOut;

varying lowp vec3 TintColorOut;
varying lowp float IntensityOut;

uniform sampler2D Texture0;

void main(void) {
    vec4 tex = texture2D(Texture0, TexCoord0);
    vec3 tinted = mix(tex.rgb, tex.rgb * TintColorOut, IntensityOut);
    gl_FragColor = Color0 * vec4(tinted, tex.a);
}
